// import libaries
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function BlogPostForm({ onPostSubmit, editingPost, clearEditingPost }) {
    const [title, setTitle] = useState('');
    const [body, setBody] = useState('');
    const [author, setAuthor] = useState('');

    // Pre-fill form fields if editing an existing post
    useEffect(() => {
        if (editingPost) {
            setTitle(editingPost.title);
            setBody(editingPost.body);
            setAuthor(editingPost.author);
        }
    }, [editingPost]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (editingPost) {
                // Edit existing post
                await axios.put(`http://localhost:8000/blogs/${editingPost.id}`, { title, body, author });
                alert('Post updated successfully');
                clearEditingPost();  // Clear editing state after successful update
            } else {
                // Create new post
                await axios.post('http://localhost:8000/blogs', { title, body, author });
                alert('Post created successfully');
            }
            setTitle('');
            setBody('');
            setAuthor('');
            onPostSubmit();  // Trigger post refresh
        } catch (error) {
            console.error('Error submitting post', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>{editingPost ? 'Edit Post' : 'Create a New Post'}</h2>
            <input
                type="text"
                placeholder="Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
            />
            <textarea
                placeholder="Body"
                value={body}
                onChange={(e) => setBody(e.target.value)}
            />
            <input
                type="text"
                placeholder="Author"
                value={author}
                onChange={(e) => setAuthor(e.target.value)}
            />
            <button type="submit">{editingPost ? 'Update Post' : 'Create Post'}</button>
            {editingPost && <button type="button" onClick={clearEditingPost}>Cancel Edit</button>}
        </form>
    );
}

export default BlogPostForm;
