// import libaries
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import './App.css';  // Import the CSS file
import Signup from './components/Signup';
import Signin from './components/Signin';
import BlogPostForm from './components/BlogPostForm';
import PostList from './components/PostList';

// function to control sign up and sign in navigation
function App() {

    // make a constant for authentification
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    // return the state of the signup/blog path
    return (
        <div className="container">
            <Router>
                <Routes>
                    <Route path="/" element={<Signin setIsAuthenticated={setIsAuthenticated} />} />
                    <Route path="/signup" element={<Signup />} />
                    <Route
                        path="/blog"
                        element={
                            isAuthenticated ? (
                                <Blog />
                            ) : (
                                <Navigate to="/" replace />
                            )
                        }
                    />
                </Routes>
            </Router>
        </div>
    );
}

// function to handle blog interactions
function Blog() {
    const [refreshPosts, setRefreshPosts] = useState(false);
    const [editingPost, setEditingPost] = useState(null);

    const handlePostSubmit = () => {
        setRefreshPosts(!refreshPosts);
        setEditingPost(null);
    };

    const handleEditPost = (post) => {
        setEditingPost(post);
    };

    const clearEditingPost = () => {
        setEditingPost(null);
    };

    return (
        <div>
            <BlogPostForm
                onPostSubmit={handlePostSubmit}
                editingPost={editingPost}
                clearEditingPost={clearEditingPost}
            />
            <PostList key={refreshPosts} onEditPost={handleEditPost} />
        </div>
    );
}

export default App;
