import React, { useState, useEffect } from 'react';
import axios from 'axios';

function PostList() {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        fetchPosts();
    }, []);

    const fetchPosts = async () => {
        const response = await axios.get('http://localhost:8000/blogs');
        setPosts(response.data);
    };

    const handleEdit = async (id) => {
        const newTitle = prompt('Enter new title');
        const newBody = prompt('Enter new body');
        if (newTitle && newBody) {
            await axios.put(`http://localhost:8000/blogs/${id}`, { title: newTitle, body: newBody });
            fetchPosts(); // Refresh the posts after editing
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this post?')) {
            await axios.delete(`http://localhost:8000/blogs/${id}`);
            fetchPosts(); // Refresh the posts after deletion
        }
    };

    return (
        <div>
            <h2>Blog Posts</h2>
            {posts.map(post => (
                <div key={post.id}>
                    <h3>{post.title}</h3>
                    <p>{post.body}</p>
                    <p><strong>Author:</strong> {post.author}</p>
                    <button onClick={() => handleEdit(post.id)}>Edit</button>
                    <button onClick={() => handleDelete(post.id)}>Delete</button>
                </div>
            ))}
        </div>
    );
}

export default PostList;
