import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Signup from './components/Signup';
import Signin from './components/Signin';
import BlogPostForm from './components/BlogPostForm';
import PostList from './components/PostList';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    return (
        <Router>
            <Routes>
                <Route path="/" element={<Signin setIsAuthenticated={setIsAuthenticated} />} />
                <Route path="/signup" element={<Signup />} />
                <Route
                    path="/blog"
                    element={
                        isAuthenticated ? (
                            <Blog />
                        ) : (
                            <Navigate to="/" replace />
                        )
                    }
                />
            </Routes>
        </Router>
    );
}

// Blog component to display BlogPostForm and PostList
function Blog() {
    const [refreshPosts, setRefreshPosts] = useState(false);

    const handlePostSubmit = () => {
        // Toggle refreshPosts to trigger PostList re-render
        setRefreshPosts(!refreshPosts);
    };

    return (
        <div>
            <BlogPostForm onPostSubmit={handlePostSubmit} />
            <PostList key={refreshPosts} /> {/* Changing key forces PostList re-render */}
        </div>
    );
}

export default App;
