import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import Signup from './components/Signup';
import Signin from './components/Signin';
import BlogPostForm from './components/BlogPostForm';
import PostList from './components/PostList';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    return (
        <Router>
            <Routes>
                {/* Only show the Signin page initially */}
                <Route path="/" element={<Signin setIsAuthenticated={setIsAuthenticated} />} />
                <Route path="/signup" element={<Signup />} />

                {/* Redirect to Blog page only if authenticated */}
                <Route
                    path="/blog"
                    element={
                        isAuthenticated ? (
                            <Blog />
                        ) : (
                            <Navigate to="/" replace />
                        )
                    }
                />
            </Routes>
        </Router>
    );
}

// Blog component that includes both BlogPostForm and PostList for the /blog page
function Blog() {
    return (
        <div>
            <BlogPostForm />
            <PostList />
        </div>
    );
}

export default App;
