import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Signup from './components/Signup';
import Signin from './components/Signin';
import BlogPostForm from './components/BlogPostForm';
import PostList from './components/PostList';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    return (
        <Router>
            <Routes>
                <Route path="/" element={<Signin setIsAuthenticated={setIsAuthenticated} />} />
                <Route path="/signup" element={<Signup />} />
                <Route
                    path="/blog"
                    element={
                        isAuthenticated ? (
                            <Blog />
                        ) : (
                            <Navigate to="/" replace />
                        )
                    }
                />
            </Routes>
        </Router>
    );
}

function Blog() {
    const [refreshPosts, setRefreshPosts] = useState(false);
    const [editingPost, setEditingPost] = useState(null);

    const handlePostSubmit = () => {
        setRefreshPosts(!refreshPosts);  // Refresh posts on submission
        setEditingPost(null);  // Clear editing state
    };

    const handleEditPost = (post) => {
        setEditingPost(post);  // Set post for editing
    };

    const clearEditingPost = () => {
        setEditingPost(null);  // Clear edit mode
    };

    return (
        <div>
            <BlogPostForm
                onPostSubmit={handlePostSubmit}
                editingPost={editingPost}
                clearEditingPost={clearEditingPost}
            />
            <PostList key={refreshPosts} onEditPost={handleEditPost} />
        </div>
    );
}


export default App;
