import React, { useState } from 'react';
import axios from 'axios';

function BlogPostForm({ onPostSubmit }) {
    const [title, setTitle] = useState('');
    const [body, setBody] = useState('');
    const [author, setAuthor] = useState(''); // Optional: Could link to authenticated user

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:8000/blogs', { title, body, author });
            setTitle('');
            setBody('');
            setAuthor('');
            alert('Post created successfully');
            onPostSubmit();  // Trigger post refresh
        } catch (error) {
            console.error('Error creating post', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Create a New Post</h2>
            <input
                type="text"
                placeholder="Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
            />
            <textarea
                placeholder="Body"
                value={body}
                onChange={(e) => setBody(e.target.value)}
            />
            <input
                type="text"
                placeholder="Author"
                value={author}
                onChange={(e) => setAuthor(e.target.value)}
            />
            <button type="submit">Create Post</button>
        </form>
    );
}

export default BlogPostForm;
